import time
import operator 
import numpy as np 

def maxValue(n, rounds):
    investimento = [0]*n
    tm=time.time()
    for rnd in rounds:
         for i in range(rnd[0]-1,rnd[1]):
             investimento[i] += rnd[2]

    print ("time 1 = " + str(time.time() - tm))
    investimento = [0]*n
    tm=time.time()
    for rnd in rounds:
        newarray = ([0]*(rnd[0]-1)) + ([rnd[2]]*(rnd[1]-rnd[0]+1)) + ([0]*(n-rnd[1]))
        investimento = [x + y for x, y in zip(investimento, newarray)]
    
    print ("time 1 = " + str(time.time() - tm))
    investimento = [0]*n
    tm=time.time()
    for rnd in rounds:
        newarray = ([0]*(rnd[0]-1)) + ([rnd[2]]*(rnd[1]-rnd[0]+1)) + ([0]*(n-rnd[1]))
        investimento = list(map(operator.add, investimento, newarray))
    
    print ("time 1 = " + str(time.time() - tm))
    investimento = [0]*n
    tm=time.time()

    for rnd in rounds:
        newarray = ([0]*(rnd[0]-1)) + ([rnd[2]]*(rnd[1]-rnd[0]+1)) + ([0]*(n-rnd[1]))
        investimento = np.add(investimento, newarray)
    print ("time 1 = " + str(time.time() - tm))
    
    return max(investimento)

